import React, { Component } from "react";
import { View, TouchableOpacity, Text } from "react-native";
import DraggableFlatList from "react-native-draggable-flatlist";
import { getCurrentFrame } from "expo/build/AR";
import FoodCard from "./FoodCard";

const allFood = [
  { name: "Apples", toGet: false, inBasket: false },
  { name: "Tofu", toGet: true, inBasket: true },
  { name: "Milk, Oat", toGet: true, inBasket: false },
];

const exampleData = allFood.map((d, index) => ({
  food: d,
  key: `item-${index}`,
  label: d.name,
  margin: 10,
}));

class Test extends Component {
  state = {
    data: exampleData,
  };

  renderItem = ({ item, index, drag, isActive }) => {
    return (
      <TouchableOpacity
        style={{
          height: 100,
          borderColor: "black",
          borderWidth: 2,
          backgroundColor: isActive ? "green" : item.backgroundColor,
          alignItems: "center",
          justifyContent: "center",
          margin: 10,
        }}
        onLongPress={drag}
      >
        <FoodCard food={item.food} key={index}></FoodCard>
      </TouchableOpacity>
    );
  };

  render() {
    return (
      <View
        style={{
          flex: 1,
          borderColor: "black",
          borderWidth: 2,
          margin: 10,
          marginTop: 50,
        }}
      >
        <DraggableFlatList
          data={this.state.data}
          renderItem={this.renderItem}
          keyExtractor={(item, index) => `draggable-item-${item.key}`}
          onDragEnd={({ data }) => this.setState({ data })}
        />
      </View>
    );
  }
}

export default Test;
