import React, { useState, useEffect } from "react";
import { View, Text, StyleSheet } from "react-native";
import FoodCard from "./FoodCard";
import DraggableFlatList from "react-native-draggable-flatlist";

const FoodCardList = ({
  food,
  toggleToGet,
  toggleBasket,
  showBasketBtn,
  inBasketCont,
}) => {
  const [foodList, setFoodList] = useState([]);

  useEffect(() => {
    setFoodList(mapFood);
  });

  const mapFood = food.map((f, index) => (
    <FoodCard
      food={food}
      key={index}
      toggleToGet={toggleToGet}
      toggleBasket={toggleBasket}
      showBasketBtn={showBasketBtn}
      inBasketCont={inBasketCont}
      key={`item-${index}`}
      label={f.name}
    ></FoodCard>
  ));

  renderItem = ({ item, index, drag, isActive }) => {
    return (
      <TouchableOpacity
        style={{
          height: 100,
          borderColor: "black",
          borderWidth: 2,
          backgroundColor: isActive ? "green" : item.backgroundColor,
          alignItems: "center",
          justifyContent: "center",
        }}
        onLongPress={drag}
      >
        <Text
          style={{
            fontWeight: "bold",
            color: "white",
            fontSize: 32,
          }}
        >
          {item.label}
        </Text>
      </TouchableOpacity>
    );
  };

  return (
    <View
      style={{
        flex: 1,
        borderColor: "black",
        borderWidth: 2,
        margin: 10,
        marginTop: 50,
      }}
    >
      <DraggableFlatList
        data={foodList}
        renderItem={renderItem}
        keyExtractor={(item, index) => `draggable-item-${item.key}`}
        onDragEnd={({ data }) => setFoodList({ data })}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  foodCardList: {
    borderColor: "grey",
    borderWidth: 1,
    width: "90%",
    alignItems: "center",
    marginTop: 10,
    padding: 20,
  },
});

export default FoodCardList;
