import React, { useState } from "react";
import { View, TouchableOpacity, Text, StyleSheet } from "react-native";
import FoodCard from "./FoodCard";
import DraggableFlatList from "react-native-draggable-flatlist";

const FoodCardList = ({
  food,
  toggleToGet,
  toggleBasket,
  showBasketBtn,
  inBasketCont,
}) => {
  const [foodCopy, setFoodCopy] = useState(food);

  // let foodList = food.map((food, index) => (
  //   <FoodCard
  //     food={food}
  //     key={index}
  //     toggleToGet={toggleToGet}
  //     toggleBasket={toggleBasket}
  //     showBasketBtn={showBasketBtn}
  //     inBasketCont={inBasketCont}
  //   ></FoodCard>
  // ));

  let renderItem = ({ item, index, drag, isActive }) => {
    return (
      <TouchableOpacity
        style={{
          height: 100,
          alignItems: "center",
          justifyContent: "center",
        }}
        onLongPress={drag}
      >
        <Text
          style={{
            fontWeight: "bold",
            color: "white",
            fontSize: 32,
          }}
        >
          {item.name}
        </Text>
      </TouchableOpacity>
    );
  };

  return (
    <View style={styles.foodCardList}>
      <DraggableFlatList
        data={foodCopy}
        renderItem={renderItem}
        keyExtractor={(item, index) => `draggable-item-${item.key}`}
        onDragEnd={({ data }) => setFoodCopy(data)}
      ></DraggableFlatList>
    </View>
  );
};

const styles = StyleSheet.create({
  foodCardList: {
    borderColor: "grey",
    borderWidth: 1,
    width: "90%",
    alignItems: "center",
    marginTop: 10,
    padding: 20,
  },
});

export default FoodCardList;
